import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null); // To store the image preview URL
  const [result, setResult] = useState(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setPreview(URL.createObjectURL(file)); // Create preview URL
    setResult(null);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("image", image);

    const response = await axios.post("http://localhost:5000/predict", formData);
    setResult(response.data);
  };

  return (
    <div className="App">
      <h1>ðŸš— Distracted Driver Detection</h1>
      <input type="file" onChange={handleFileChange} accept="image/*" />
      <button onClick={handleUpload} disabled={!image}>Predict</button>

      {preview && (
        <div style={{ marginTop: '20px' }}>
          <h3>Preview:</h3>
          <img
            src={preview}
            alt="Uploaded Preview"
            style={{ width: '300px', borderRadius: '8px' }}
          />
        </div>
      )}

      {result && (
        <div className={`alert ${result.is_distracted ? "alert-danger" : "alert-safe"}`}>
          <h2>{result.is_distracted ? "ðŸš¨ DISTRACTED!" : "âœ… Safe Driving"}</h2>
          <p><strong>Behavior:</strong> {result.label}</p>
          <p><strong>Confidence:</strong> {(result.confidence * 100).toFixed(2)}%</p>
        </div>
      )}
    </div>
  );
}

export default App;
