import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [inputStatus, setInputStatus] = useState(null);

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
    setResult(null);
    setInputStatus(null);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("image", image);

    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/predict", formData);
      setResult(response.data);
      setInputStatus("success");

      if (response.data.is_distracted) {
        const alertSound = new Audio("/alert.mp3");
        alertSound.play();
      }
    } catch (error) {
      console.error("Error uploading image:", error);
      setInputStatus("error");
    }
    setLoading(false);
  };

  return (
    <div className="app-wrapper">
      <video autoPlay loop muted className="background-video">
        <source src="/background.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <div className="App">
        {/* Your content remains the same */}
        <div className="header">
          <h1 className="title">Efficient Distracted Driver Behavior Identification Model</h1>
          <p className="subtitle">Upload a photo to detect distracted driving behavior</p>
        </div>
        {/* ... rest of the component */}
      </div>
    </div>
  );
}

export default App;
