import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
    setResult(null);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("image", image);

    const response = await axios.post("http://localhost:5000/predict", formData);
    setResult(response.data);
  };

  return (
    <div className="App">
      <h1>ðŸš— Distracted Driver Detection</h1>
      <input type="file" onChange={handleFileChange} accept="image/*" />
      <button onClick={handleUpload} disabled={!image}>Predict</button>

      {result && (
        <div className={`alert ${result.is_distracted ? "alert-danger" : "alert-safe"}`}>
          <h2>{result.is_distracted ? "ðŸš¨ DISTRACTED!" : "âœ… Safe Driving"}</h2>
          <p><strong>Behavior:</strong> {result.label}</p>
          <p><strong>Confidence:</strong> {(result.confidence * 100).toFixed(2)}%</p>
        </div>
      )}
    </div>
  );
}

export default App;
