import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
    setResult(null);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("image", image);

    const response = await axios.post("http://localhost:5000/predict", formData);
    setResult(response.data);
  };

  return (
    <div className="App">
      <div className="header">
        <h1 className="title">Efficient Distracted Driver Detection</h1>
      </div>
      <div className="content">
        <div className="input-section">
          <input 
            type="file" 
            onChange={handleFileChange} 
            accept="image/*" 
            className="upload-input" 
          />
          <button 
            onClick={handleUpload} 
            disabled={!image} 
            className="upload-button"
          >
            Predict
          </button>
        </div>

        {image && (
          <div className="preview-section">
            <h3 className="preview-title">Uploaded Image Preview:</h3>
            <img 
              src={URL.createObjectURL(image)} 
              alt="Uploaded" 
              className="image-preview" 
            />
          </div>
        )}

        {result && (
          <div className={`result-card ${result.is_distracted ? "distracted" : "safe"}`}>
            <h2>{result.is_distracted ? "ðŸš¨ DISTRACTED!" : "âœ… Safe Driving"}</h2>
            <p><strong>Behavior:</strong> {result.label}</p>
            <p><strong>Confidence:</strong> {(result.confidence * 100).toFixed(2)}%</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
