import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [inputStatus, setInputStatus] = useState(null);

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
    setResult(null);
    setInputStatus(null);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("image", image);

    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/predict", formData);
      setResult(response.data);
      setInputStatus("success");

      if (response.data.is_distracted) {
        const alertSound = new Audio("/alert.mp3");
        alertSound.play();
      }
    } catch (error) {
      console.error("Error uploading image:", error);
      setInputStatus("error");
    }
    setLoading(false);
  };

  return (
    <div className="background">
      <div className="App">
        <div className="header">
          <h1 className="title">Efficient Distracted Driver De</h1>
          <p className="subtitle">Upload a photo to detect distracted driving behavior</p>
        </div>

        <div className="input-section">
          <input
            type="file"
            onChange={handleFileChange}
            accept="image/*"
            className={`upload-input ${inputStatus === "success" ? "success" : inputStatus === "error" ? "error" : ""}`}
          />
          <button onClick={handleUpload} disabled={!image} className="upload-button">
            Detect
          </button>
        </div>

        {loading && <div className="loader"></div>}

        {image && (
          <div className="preview-section">
            <h3 className="preview-title">Image Preview</h3>
            <img
              src={URL.createObjectURL(image)}
              alt="Preview"
              className="image-preview"
            />
          </div>
        )}

        {result && (
          <div className={`result-card ${result.is_distracted ? "distracted" : "safe"} show`}>
            <h2>{result.is_distracted ? "ðŸš¨ Distracted!" : "âœ… Safe Driving"}</h2>
            <p><strong>Behavior:</strong> {result.label}</p>
            <p><strong>Confidence:</strong> {(result.confidence * 100).toFixed(2)}%</p>
          </div>
        )}

        <div className="footer">
          <p>&copy; 2025 Distracted Driver Detection | All Rights Reserved</p>
        </div>
      </div>
    </div>
  );
}

export default App;
