import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setPreview(URL.createObjectURL(file));
    setResult(null);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("image", image);

    const response = await axios.post("http://localhost:5000/predict", formData);
    setResult(response.data);
  };

  return (
    <div className="app-container">
      <div className="header">
        <h1>ðŸš— Distracted Driver Detection</h1>
      </div>

      <div className="upload-section">
        <input
          type="file"
          onChange={handleFileChange}
          accept="image/*"
          className="file-input"
        />
        <button
          onClick={handleUpload}
          disabled={!image}
          className="upload-btn"
        >
          Predict
        </button>
      </div>

      {preview && (
        <div className="preview-container">
          <h3>Image Preview:</h3>
          <img src={preview} alt="Uploaded Preview" className="preview-img" />
        </div>
      )}

      {result && (
        <div className={`result-alert ${result.is_distracted ? "alert-danger" : "alert-safe"}`}>
          <h2>{result.is_distracted ? "ðŸš¨ DISTRACTED!" : "âœ… Safe Driving"}</h2>
          <p><strong>Behavior:</strong> {result.label}</p>
          <p><strong>Confidence:</strong> {(result.confidence * 100).toFixed(2)}%</p>
        </div>
      )}
    </div>
  );
}

export default App;
